            ObRep.Foto = buffer;

            await resizedFile.OpenReadStream().ReadAsync(buffer);

            var imageDataUrl = $"data:{format};base64,{Convert.ToBase64String(buffer)}";

            imageDateUrls.Add(imageDataUrl);
        }
